function Sim = Settings(Sim)  do    choice = menu("Settings",{"Source file","BUSV..","Fragments..","Wind profile..","Back"});    switch choice      case 1        Sim.SourceFile = SourceFile(Sim);      case 2        Sim.BUSV = BUSV(Sim);      case 3        Sim.Fragments = Fragments(Sim);      case 4        Sim.WindProfile = WindProfileGenerator();    endswitch  until choice == 0 || choice == 5endfunction